{{-- style trending-products --}}
<style>@import url('https://fonts.googleapis.com/css2?family=Edu+NSW+ACT+Foundation:wght@600&family=Nunito&display=swap');.trending-product {width: 100%;height: auto;padding-bottom: 50px;}.title-section{font-size: 41px;font-family: 'Edu NSW ACT Foundation', cursive;font-style: italic;color: black;text-align: center;margin-top: 10px;text-decoration: underline black;margin-bottom: 50px;}#trending-product {width: 100%;height: auto;display: flex;justify-content: space-evenly;flex-wrap: wrap;padding-bottom: 20px;}.product {margin-top: 50px;width: 17%;height: auto;border: 1px solid #2a2478;border-radius: 5px;padding: 5px;}.img-product {width: 100%;height: 255px;}#product-shop {width: 80%;height: auto;margin-left: 23px;margin-right: auto;}.name-shop {width: 100%;height: 64px;}.name-product {font-size: 14px;font-family: 'Nunito', sans-serif;color: black;margin-top: 10px;text-transform: capitalize;}.category-shop {width: 100%;height: 33px;}.category-product {text-transform: capitalize;font-size: 15px;font-family: 'Nunito', sans-serif;color: black;margin-top: 3px;font-weight: 100;}.flex {display: flex;justify-content: space-between;align-items: center;width: 100%;height: auto;padding-bottom: 10px;}.price-product {font-size: 18px;font-family: 'Nunito', sans-serif;color: black;font-weight: bold;margin-top: 3px;}#btn-add {width: 40px;height: 40px;cursor: pointer;border: none;border-radius: 50%;background: #2a2478;}#btn-add:active {transform: scale(0.98);}ion-icon[name= 'add'] {color: white;width: 30px;height: 30px;}@media screen and (min-width: 320px) and (max-width: 408px) {#trending-product {display: flex;flex-direction: column;}.title-section{font-size: 27px;}.product {width: 95%;margin-left: auto;margin-right: auto;}.img-product {height: 270px;}}@media screen and (min-width: 409px) and (max-width: 484px) {#trending-product {display: flex;flex-direction: column;}.title-section{font-size: 27px;}.product {width: 95%;margin-left: auto;margin-right: auto;}.img-product {height: 37