<style>@import url('https://fonts.googleapis.com/css2?family=Roboto&display=swap');.Form-section{width: 100%;height: auto;padding-top: 50px;padding-bottom: 50px;}.input-data{width: 30%;height: auto;padding: 10px;margin-left: auto;margin-right: auto;border: 1px solid #2a2478;border-radius: 5px;}.input{width: 100%;height: auto;display: flex;justify-content: center;}#input{width: 80%;height: 40px;margin-top: 20px;margin-bottom: 20px;border-radius: 5px;background: none;border: 1px solid #2a2478;color: black;padding-left: 20px;font-size: 15px;font-family: 'Roboto', sans-serif;}#input:focus{outline: none;}#input::placeholder{text-transform: capitalize;}.btn-checkout{display: grid;justify-items: center;width: 100%;padding: 10px;}.button-checkout{width: 80%;height: 36px;font-size: 16px;font-family: 'Roboto', sans-serif;margin-left: 56px;margin-right: 56px;color: white;background: #2a2478;border-radius: 5px;border: none;cursor: pointer;}.button-checkout:active{transform: scale(0.98)}@media screen and (min-width: 320px) and (max-width: 900px){.input-data{width: 98%;}.button-checkout{margin-left: auto;margin-right: auto;}}</style>
