{{-- style navbar-shop --}}
<style>.navbar-shop{width: 100%;height: 150px;background-origin: border-box;background-size: cover;background-repeat: no-repeat;background-image: url('../assets/shop/shop-cover.jpg');margin-bottom: 50px;}</style>
{{-- style product shop  --}}
<style>@import url('https://fonts.googleapis.com/css2?family=Edu+NSW+ACT+Foundation:wght@600&family=Nunito&display=swap');.products-shop {width: 100%;height: auto;padding-bottom: 50px;}.product-shop{width: 100%;height: auto;display: flex;justify-content: space-evenly;flex-wrap: wrap;padding-bottom: 20px;}#product-shop {margin-top: 50px;width: 17%;height: auto;border: 1px solid #2a2478;border-radius: 5px;padding: 5px;}.product-image-shop {width: 100%;height: 255px;}#product-image-shop {margin-bottom: 39px;width: 80%;height: auto;margin-left: 23px;margin-right: auto;}.name-product-shop{width: 100%;height: 64px;}#name-product-shop {font-size: 14px;font-family: 'Nunito', sans-serif;color: black;margin-top: 10px;text-transform: capitalize;}.category-product-shop{width: 100%;height: 33px;}#category-product-shop {text-transform: capitalize;font-size: 15px;font-family: 'Nunito', sans-serif;color: black;margin-top: 3px;font-weight: 100;}.flex-shop {display: flex;justify-content: space-between;align-items: center;width: 100%;height: auto;padding-bottom: 10px;}#price-product-shop {font-size: 18px;font-family: 'Nunito', sans-serif;color: black;font-weight: bold;margin-top: 3px;}#btn-add-shop {width: 40px;height: 40px;cursor: pointer;border: none;border-radius: 50%;background: #2a2478;}#btn-add-shop:active {transform: scale(0.98);}.add {color: white;width: 30px;height: 30px;}@media screen and (min-width: 320px) and (max-width: 408px){.product-shop{display: flex;flex-direction: column;}#product-shop {width: 95%;margin-left: auto;margin-right: auto;}.product-image-shop {margin-bottom: 39px;height: 270px;}}@media screen and (min-width: 409px) and (max-width: 484px){.product-shop{display: flex;flex-direction: column;}#product-shop {width: 95%;margin-left: auto;margin-right: auto;}.product-image-shop {margin-bottom: 39px;height: 377px;}}@media screen and (min-width: 485px) and (max-width: 608px) {.product-shop{display: flex;flex-direction: column;}#product-shop {width: 95%;margin-left: auto;margin-right: auto;}.product-image-shop {height: 468px;}}@media screen and (min-width: 705px) and (max-width: 900px) {.product-shop{display: flex;flex-direction: column;}#product-shop {width: 95%;margin-left: auto;margin-right: auto;}.product-image-shop {height: 468px;}}@media screen and (min-width: 901px) and (max-width: 1200px) {#product-shop {width: 32%;}.product-image-shop {width: 100%;height: 308px;}}</style>
{{-- style search --}}
<style>@import url('https://fonts.googleapis.com/css2?family=Ubuntu&display=swap');.search{width: 100%;height: auto;padding-bottom: 10px;padding-top: 10px;}#search{width: 100%;height: auto;}.search-select{width: 140%;height: 38px;background: #2a2478;color: white;text-align: center;font-size: 15px;font-family: 'Ubuntu', sans-serif;border: 1px solid #2a2478;border-radius: 5px;}.search-select:focus{outline: none;}.search-input{width: 23%;display: flex;margin-left: auto;margin-right: auto; height: auto;justify-content: center;}#search-input{width: 76%;height: 44px;margin-right: 5px;border: none;border-radius: 5px;padding-left: 10px;font-family: 'Ubuntu', sans-serif;background: #2a2478;color: white;}#search-input:focus{outline: none;}#search-input::placeholder{font-size: 15px;font-family: 'Ubuntu', sans-serif;color: white;}.btn-search{width: 16%;height: 44px;background: #2a2478;border: none;border-radius: 5px;cursor: pointer;}.btn-search:active{transform: scale(0.98);}ion-icon[name = 'search'] {color: white;width: 20px;height: 20px;}@media screen and (min-width: 320px) and (max-width: 900px){.search-select{width: 73%;font-size: 11px;}.search-input{width: 100%;}}</style>
{{-- div success --}}
<style>@import url('https://fonts.googleapis.com/css2?family=Lato&display=swap');.success{width: 100%;height: auto;}#success{background: #2fa72f;width: 98%;margin-left: auto;margin-right: auto;padding: 10px;border-radius: 5px;border: 3px solid green;}.msg{font-size: 18px;text-align: center;font-family: 'Lato', sans-serif;color: white;font-weight: normal;}</style>
